const app = new Vue({
    el: "#app",

    data: {
        counter: 0,
        images: [
            "https://images.everyeye.it/img-notizie/marte-sottosuolo-pianeta-offerto-ideale-vita-v3-485698-1280x720.jpg",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
        ]
    },

    methods: {
        prev() {
            if (this.counter === 0) {
                return this.counter = this.images.length -1;
            }
            return this.counter--;
        },

        next() {
            if (this.counter === this.images.length -1) {
                return this.counter = 0;
            }
            return this.counter++;
        }
    }
});

for (let i = 0; i < app.images.length; i++) {
    const markup = `<div class="pallino" data-index="${i}"></div>`;
    document.getElementById("pallini").insertAdjacentHTML("beforeend", markup);    
}